import fetch from "node-fetch";

function Blog({ posts }) {
  return (
    <ul>
      {posts.map((post) => {
        return <li>{post.Title}</li>;
      })}
    </ul>
  );
}

export async function getStaticProps() {
  const res = await fetch("http://www.omdbapi.com/?s=avengers&apikey=158fe49");
  const data = await res.json();
  const posts = await data.Search;

  return {
    props: {
      posts,
    },
  };
}

export default Blog;
