// import fetch from "node-fetch";
import Link from "next/link";

function Blog({ posts }) {
    return(
        <ul>
            {posts.map((post) => {
                const split = post.Poster.split("/");
                const poster = split.slice(-1)[0];
                return (
                    <li>
                        <Link 
                        href="/img/[poster]" 
                        as={`img/${poster}`} 
                        target="_blank">
                            <a><h2>{post.Title}</h2></a>
                        </Link>
                    </li>
            )
            })}
        </ul>
    );
}

