export async function getServerSideProps() {
    const res = await fetch("http://www.omdbapi.com/?s=avengers&apikey=d3353ed0");
    const data = await res.json();
    const posts = await data.Search;
    return {
        props: {
            posts,
        }
    }
}

export default Blog;
